{"filter":false,"title":"codegen.py","tooltip":"/mydiycc/my011/codegen.py","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":74,"column":0},"action":"insert","lines":["def walk( tree , f):","    if (tree.label == 'NUM'):","        f.write('   pushq $'+tree.items[0]+ \"\\n\")","    elif (tree.label == '+'):","        walk(tree.items[0],f)","        walk(tree.items[1],f)","        f.write(r'   pop %rdi' + \"\\n\")","        f.write(r'   pop %rax' + \"\\n\")","        f.write(r'   add %rdi,%rax' + \"\\n\")","        f.write(r'   pushq %rax' + \"\\n\")","    elif (tree.label == '-'):","        walk(tree.items[0],f)","        walk(tree.items[1],f)","        f.write(r'   pop %rdi' + \"\\n\")","        f.write(r'   pop %rax' + \"\\n\")","        f.write(r'   sub %rdi,%rax' + \"\\n\")","        f.write(r'   pushq %rax' + \"\\n\")","    elif (tree.label == '*'):","        walk(tree.items[0],f)","        walk(tree.items[1],f)","        f.write(r'   pop %rdi' + \"\\n\")","        f.write(r'   pop %rax' + \"\\n\")","        f.write(r'   imul %rdi,%rax' + \"\\n\")","        f.write(r'   pushq %rax' + \"\\n\")","    # elif (tree.label == '/'):","    #     walk(tree.items[0],f)","    #     walk(tree.items[1],f)","    #     f.write(r'   pop %rdi' + \"\\n\")","    #     f.write(r'   pop %rax' + \"\\n\")","    #     f.write(r'   sub %rdi,%rax' + \"\\n\")","    #     f.write(r'   pushq %rax' + \"\\n\")","","def codegen( tree , filename ):","    with open(filename, mode='w') as f:","","        ## １個目は関数の定義とする","        teigi1 = tree[0]","","        ##","        print('===codegen step1===')","        print(teigi1)","        rettype  = teigi1.items[0]    #戻り値の型 int","        funcname = teigi1.items[1]    #関数名 main","","        exp = []","        returnexp = list( filter( lambda item: item.label != 'SENGEN' , teigi1.items[2] ) )","        print(returnexp)","        exp = returnexp[0].items[0]","        # exp      = teigi1[3][2][1] #文","        print(exp)","","        print('===codegen step2===')","        print('funcname: '+funcname)","","        #asm の先頭","        f.write(r'   .text' + \"\\n\")","","        #関数ヘッダ","        f.write(r'   .globl\t'+ funcname + \"\\n\")","        f.write(r'   .type\t'+ funcname + ', @function' + \"\\n\")","        f.write(funcname + \":\\n\")","        f.write(r'   endbr64' + \"\\n\")","        f.write(r'   pushq %rbp' + \"\\n\")","        f.write(r'   movq %rsp, %rbp' + \"\\n\")","","        #式を歩く","        walk(exp,f)","        f.write(r'   pop\t%rax'+ \"\\n\")","","        #関数フッダ","        f.write(r'   popq\t%rbp'+ \"\\n\")","        f.write(r'   ret'+ \"\\n\")","        f.write(r'   .size '+funcname+', .-'+ funcname +\"\\n\")","",""],"id":1}]]},"ace":{"folds":[],"scrolltop":586.1000000000003,"scrollleft":0,"selection":{"start":{"row":74,"column":0},"end":{"row":74,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":38,"state":"start","mode":"ace/mode/python"}},"timestamp":1693922852524,"hash":"63072e9061e4365e82d6aeb6e457a7a1632d4447"}